
public class Dowhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int	x=5;
	int	i=-10;
		do {
			System.out.print("hello\t");
			System.out.print(i + "\t");
			System.out.print(Math.pow(x, i)+ "\t\t");
			System.out.println("hello" + i);
				i=i-2;
		}while(i>0);

	}

}
