package Homework;

public class OddEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0;i<=30;i++) {
			if(i%2==1){
				System.out.println(i +"\t"+ "This is a odd number");
				}
			
			  }
		
		for(int i=0;i<=30;i++) {
			if(i%2==0) {
				System.out.println(i + "\t" + "This is a even number");
			}
		}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		

	}

}
